import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaGoogle, FaFacebookF, FaApple, FaWindows } from "react-icons/fa";
import "./RoleSelection.css";

function PatientLogin() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({});
  const [isForgotPassword, setIsForgotPassword] = useState(false);  // ***

  // Login handler (same as your existing one)
  const handleLogin = (e) => {
    e.preventDefault();
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    let newErrors = {};

    if (!email) {
      newErrors.email = "Email address is required.";
    } else if (!emailPattern.test(email)) {
      newErrors.email = "Invalid email format.";
    }

    if (!password) {
      newErrors.password = "Password is required.";
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      if (email === "abc@gmail.com" && password === "ABCabc123#") {
        alert("Login successful!");
        navigate("/dashboard");
      } else {
        setErrors({ general: "Incorrect email or password." });
      }
    }
  };

  // Forgot Password handler ***
  const handleResetPassword = (e) => {
    e.preventDefault();
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    let newErrors = {};

    if (!email) {
      newErrors.email = "Email address is required.";
    } else if (!emailPattern.test(email)) {
      newErrors.email = "Invalid email format.";
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      // Simulate password reset email step
      alert(`Password reset link sent to ${email}`);
      setIsForgotPassword(false);
      setEmail("");
      setErrors({});
    }
  };

  return (
    <div style={styles.body}>
      {/* Left Section (You can customize content here) */}
      <div style={styles.leftSection}>
        <h1 style={styles.leftTitle}>Medicore</h1>
        <img src="stethoscope.png.png" alt="Stethoscope" style={styles.image} />
        <p style={styles.leftText}>
          Your Journey to Better Health <br />
          Stay informed—access records, reports, appointments and connect with
          doctors securely.
        </p>
      </div>

      {/* Right Section */}
      <div style={styles.rightSection}>
        {/* Conditional rendering based on isForgotPassword */}
        {!isForgotPassword ? (
          <>
            <h1 style={styles.rightTitle}>Log in to Patient Portal</h1>
            <p style={styles.rightText}>One portal for all your healthcare needs.</p>

            <form style={styles.form} onSubmit={handleLogin}>
              {/* Email Input */}
              <input
                type="email"
                placeholder="Email"
                style={{
                  ...styles.input,
                  border: errors.email ? "1px solid red" : "1px solid #1f7a6d",
                }}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              {errors.email && <p style={styles.error}>{errors.email}</p>}

              {/* Password Input */}
              <div style={styles.passwordWrapper}>
                <input
                  type={showPassword ? "text" : "password"}
                  placeholder="Password"
                  style={{
                    ...styles.input,
                    border: errors.password ? "1px solid red" : "1px solid #1f7a6d",
                  }}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
                <span
                  style={styles.showPassword}
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? "Hide" : "Show"}
                </span>
              </div>
              {errors.password && <p style={styles.error}>{errors.password}</p>}

              {/* General error */}
              {errors.general && <p style={styles.error}>{errors.general}</p>}

              <button type="submit" style={styles.loginButton}>
                Log in
              </button>
            </form>

            {/* Social Login Section */}
            <p style={styles.loginWithText}>Log in with</p>
            <div style={styles.socialIcons}>
              <div style={{ ...styles.icon, background: "#1f7a6d" }}>
                <FaGoogle />
              </div>
              <div style={{ ...styles.icon, background: "#1f7a6d" }}>
                <FaFacebookF />
              </div>
              <div style={{ ...styles.icon, background: "#1f7a6d" }}>
                <FaApple />
              </div>
              <div style={{ ...styles.icon, background: "#1f7a6d" }}>
                <FaWindows />
              </div>
            </div>

            {/* Forgot password link - toggles to reset password form */}
            <p
              style={styles.forgotPassword}
              onClick={() => {
                setErrors({});
                setIsForgotPassword(true);
                setEmail("");
                setPassword("");
              }}
            >
              Forgot password?
            </p>

            <p style={styles.signupText}>
              Don’t have an account?{" "}
              <span
                style={styles.createLink}
                onClick={() => navigate("/signup")}
              >
                create one now
              </span>
            </p>
          </>
        ) : (
          // Reset Password Form ***
          <>
            <h1 style={styles.rightTitle}>Reset Password</h1>
            <p style={{ ...styles.rightText, marginBottom: "20px" }}>
              Enter your email to receive a password reset link
            </p>

            <form style={styles.form} onSubmit={handleResetPassword}>
              <input
                type="email"
                placeholder="Email"
                style={{
                  ...styles.input,
                  border: errors.email ? "1px solid red" : "1px solid #1f7a6d",
                }}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              {errors.email && <p style={styles.error}>{errors.email}</p>}

              <button type="submit" style={styles.loginButton}>
                Send Link
              </button>
            </form>

            <p
              style={{ ...styles.forgotPassword, marginTop: "10px" }}
              onClick={() => {
                setErrors({});
                setIsForgotPassword(false);
                setEmail("");
              }}
            >
              Back to Login
            </p>

            <p style={styles.signupText}>
              Don’t have an account?{" "}
              <span
                style={styles.createLink}
                onClick={() => navigate("/signup")}
              >
                create one now
              </span>
            </p>
          </>
        )}
      </div>
    </div>
  );
}

// No changes needed here, reuse your existing styles
const styles = {
  body: {
    display: "flex",
    height: "100vh",
    backgroundColor: "#f5f5f5",
    fontFamily: "Montserrat, Arial, sans-serif",
  },
  leftSection: {
    width: "45%",
    backgroundColor: "#1f7a6d",
    color: "white",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    padding: "40px",
  },
  leftTitle: {
    fontSize: "28px",
    marginBottom: "20px",
  },
  image: {
    width: "90%",
    margin: "20px 0",
  },
  leftText: {
    fontSize: "15px",
    color: "#dcdcdc",
    maxWidth: "350px",
  },
  rightSection: {
    width: "55%",
    backgroundColor: "white",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    padding: "40px",
    textAlign: "center",
  },
  rightTitle: {
    fontSize: "24px",
    marginBottom: "10px",
    fontWeight: "bold",
  },
  rightText: {
    fontSize: "14px",
    color: "#0a4a3f",
    marginBottom: "20px",
    maxWidth: "350px",
  },
  form: {
    width: "100%",
    maxWidth: "350px",
    display: "flex",
    flexDirection: "column",
  },
  input: {
    width: "350px",
    padding: "12px",
    margin: "10px 0",
    borderRadius: "12px",
    fontSize: "14px",
    outline: "none",
  },
  passwordWrapper: {
    position: "relative",
    width: "350px",
  },
  showPassword: {
    position: "absolute",
