import React, { useState } from "react";

function PatientLogin() {
  const [step, setStep] = useState("login"); // "login" | "forgot" | "linkSent" | "reset"
  const [email, setEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isResent, setIsResent] = useState(false);

  // Simulate sending reset link
  const handleSendLink = (e) => {
    e.preventDefault();
    if (email.trim() !== "") {
      setStep("linkSent");
    }
  };

  const handleResend = () => {
    setIsResent(true);
    setTimeout(() => setIsResent(false), 2000);
  };

  const handleResetPassword = (e) => {
    e.preventDefault();
    const isLetter = /[A-Za-z]/.test(newPassword);
    const isNumber = /\d/.test(newPassword);
    const isSpecial = /[^A-Za-z0-9]/.test(newPassword);
    const isLength = newPassword.length >= 8;

    if (isLetter && isNumber && isSpecial && isLength && newPassword === confirmPassword) {
      alert("Password reset successfully!");
      setStep("login");
      setNewPassword("");
      setConfirmPassword("");
    } else {
      alert("Please meet all password requirements and ensure both passwords match.");
    }
  };

  return (
    <div style={styles.container}>
      {/* LEFT SIDE */}
      <div style={styles.left}>
        <h2 style={{ color: "white" }}>Medicore</h2>
        <img
