          alt="stethoscope"
          style={{ width: "90%", margin: "20px 0" }}
        />
        <h3 style={{ color: "white" }}>Smarter Care Starts Here</h3>
        <p style={{ color: "#cfe8e4" }}>
          MediCore unites patients and professionals — simplifying care, records, and billing.
        </p>
        <div className="carousel-dots">
          <div className="dot active"></div>
          <div className="dot"></div>
          <div className="dot"></div>
        </div>
      </div>

      {/* RIGHT SIDE */}
      <div style={styles.right}>
        {step === "login" && (
          <>
            <h2>Log in to Patient Portal</h2>
            <form style={styles.form}>
              <input
                type="email"
                placeholder="Email"
                style={styles.input}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <input
                type="password"
                placeholder="Password"
                style={styles.input}
              />
              <button style={styles.button}>Log in</button>
            </form>
            <p style={styles.link} onClick={() => setStep("forgot")}>
              Forgot Password?
            </p>
            <p style={{ fontSize: "13px" }}>
              Don’t have an account?{" "}
              <span style={styles.link} onClick={() => setStep("reset")}>
                Create one now
              </span>
            </p>
          </>
        )}

        {step === "forgot" && (
          <>
            <h2>Reset Password</h2>
            <p style={{ fontSize: "14px" }}>
              Enter your email to receive a password reset link.
            </p>
            <form style={styles.form} onSubmit={handleSendLink}>
              <input
                type="email"
                placeholder="Enter your email"
                style={styles.input}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <button style={styles.button} type="submit">
                Send Link
              </button>
            </form>
            <p style={styles.link} onClick={() => setStep("login")}>
              Back to Login
            </p>
          </>
        )}

        {step === "linkSent" && (
          <div style={styles.emailSentBox}>
            <img
              src="https://cdn-icons-png.flaticon.com/512/561/561127.png"
              alt="email icon"
              style={styles.icon}
            />
            <h2>Email Sent</h2>
            <p style={styles.description}>
              An email with a password reset link has been sent to{" "}
              <b>{email}</b>. Open it to reset your password.
            </p>
            <button
              style={styles.openButton}
              onClick={() => setStep("reset")}
            >
              Open Email
            </button>
            <p style={styles.resendText}>
              Didn’t receive email?{" "}
              <span style={styles.resendLink} onClick={handleResend}>
                Resend Email
              </span>
            </p>
            {isResent && <p style={styles.feedback}>Email resent successfully!</p>}
          </div>
        )}

        {step === "reset" && (
          <>
            <h2>Create a New Password</h2>
            <p style={{ fontSize: "14px" }}>
              Enter and confirm your new password to regain access to your Patient Portal.
            </p>
            <form style={styles.form} onSubmit={handleResetPassword}>
              <div style={{ position: "relative" }}>
                <input
                  type={showNewPassword ? "text" : "password"}
                  placeholder="New Password"
                  style={{ ...styles.input, width: "350px" }}
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                />
                <span
                  style={styles.show}
                  onClick={() => setShowNewPassword(!showNewPassword)}
                >
                  {showNewPassword ? "Hide" : "Show"}
                </span>
              </div>

              <ul style={styles.passwordRules}>
                <li style={{ color: /[A-Za-z]/.test(newPassword) ? "green" : "red" }}>1 letter</li>
                <li style={{ color: /\d/.test(newPassword) ? "green" : "red" }}>1 number</li>
                <li style={{ color: /[^A-Za-z0-9]/.test(newPassword) ? "green" : "red" }}>1 special character</li>
                <li style={{ color: newPassword.length >= 8 ? "green" : "red" }}>8 characters</li>
              </ul>

              <div style={{ position: "relative" }}>
                <input
                  type={showConfirmPassword ? "text" : "password"}
                  placeholder="Confirm New Password"
                  style={{ ...styles.input, width: "350px" }}
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                />
                <span
                  style={styles.show}
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                >
                  {showConfirmPassword ? "Hide" : "Show"}
                </span>
              </div>

              <button style={styles.button} type="submit">
                Reset Password
              </button>
            </form>
            <p style={styles.link} onClick={() => setStep("login")}>
              Cancel and go back to Login
            </p>
          </>
        )}
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    height: "100vh",
    fontFamily: "Arial, sans-serif",
  },
  left: {
    width: "45%",
    background: "#1f7a6d",
    padding: "40px",
    color: "white",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
  },
  right: {
    width: "55%",
    background: "white",
    padding: "40px",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    textAlign: "center",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "15px",
    width: "100%",
    maxWidth: "350px",
  },
  input: {
    padding: "12px",
    border: "1px solid #1f7a6d",
    borderRadius: "10px",
    fontSize: "14px",
    outline: "none",
  },
  button: {
    padding: "12px",
    background: "#1f7a6d",
    color: "white",
    border: "none",
    borderRadius: "15px",
    cursor: "pointer",
    fontSize: "16px",
  },
  emailSentBox: {
    maxWidth: "400px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  icon: {
    width: "80px",
    marginBottom: "15px",
  },
  description: {
    fontSize: "14px",
    color: "#333",
    marginBottom: "20px",
  },
  openButton: {
    padding: "12px 30px",
    background: "#1f7a6d",
    color: "white",
    border: "none",
    borderRadius: "20px",
    cursor: "pointer",
    fontSize: "16px",
    marginBottom: "10px",
  },
  resendText: {
    fontSize: "13px",
    color: "#444",
  },
  resendLink: {
    color: "#1f7a6d",
    cursor: "pointer",
    textDecoration: "underline",
  },
  feedback: {
    marginTop: "10px",
    fontSize: "13px",
    color: "green",
  },
  show: {
    position: "absolute",
    right: "12px",
    top: "50%",
    transform: "translateY(-50%)",
    fontSize: "13px",
    color: "#1f7a6d",
    cursor: "pointer",
  },
  link: {
    color: "#1f7a6d",
    cursor: "pointer",
    marginTop: "10px",
    fontSize: "13px",
  },
  passwordRules: {
    textAlign: "left",
    fontSize: "13px",
    margin: "0 0 10px 0",
    padding: "0 20px",
    listStyleType: "disc",
  },
};

export default PatientLogin;
