    position: "relative",
    width: "350px",
  },
  showPassword: {
    position: "absolute",
    right: "15px",
    top: "50%",
    transform: "translateY(-50%)",
    color: "#1f7a6d",
    cursor: "pointer",
    fontSize: "14px",
  },
  error: {
    color: "red",
    fontSize: "13px",
    margin: "-5px 0 10px 0",
    textAlign: "left",
  },
  loginButton: {
    width: "100%",
    padding: "12px",
    margin: "20px 0",
    border: "none",
    borderRadius: "15px",
    background: "#1f7a6d",
