import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaGoogle, FaFacebookF, FaApple, FaWindows } from "react-icons/fa";
import { MdEmail } from "react-icons/md"; // Email icon

function PatientLogin() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({});
  const [isForgotPassword, setIsForgotPassword] = useState(false);
  const [emailSent, setEmailSent] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [newPassword, setNewPassword] = useState("");
  const [confirmNewPassword, setConfirmNewPassword] = useState("");
  const [resetSuccess, setResetSuccess] = useState(false);

  // Validate email with regex
  const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  // Login handler
  const handleLogin = (e) => {
    e.preventDefault();
    let newErrors = {};

    if (!email) {
      newErrors.email = "Email address is required.";
    } else if (!emailPattern.test(email)) {
      newErrors.email = "Invalid email format.";
    }

    if (!password) {
      newErrors.password = "Password is required.";
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      // Dummy login validation for example
      if (email === "abc@gmail.com" && password === "ABCabc123#") {
        alert("Login successful!");
        navigate("/dashboard");
      } else {
        setErrors({ general: "Incorrect email or password." });
      }
    }
  };

  // Forgot password: send reset link
  const handleResetPassword = (e) => {
    e.preventDefault();
    let newErrors = {};

    if (!email) {
      newErrors.email = "Email address is required.";
    } else if (!emailPattern.test(email)) {
      newErrors.email = "Invalid email format.";
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      setEmailSent(true);
      setErrors({});
    }
  };

  // Resend email link handler
  const handleResendEmail = () => {
    alert(`Password reset link resent to ${email}`);
  };

  // Open user's email provider in new tab
  const handleOpenEmail = () => {
    const domain = email.split("@")[1];
    let mailUrl = "https://mail.google.com"; // default fallback
    if (domain?.includes("gmail.com")) mailUrl = "https://mail.google.com";
    else if (domain?.includes("yahoo.com")) mailUrl = "https://mail.yahoo.com";
    else if (domain?.includes("outlook.com") || domain?.includes("hotmail.com"))
      mailUrl = "https://outlook.live.com";
    else if (domain?.includes("icloud.com")) mailUrl = "https://www.icloud.com/mail";

    window.open(mailUrl, "_blank");
  };

  // Submit new password form
  const handleSubmitNewPassword = (e) => {
    e.preventDefault();
    let newErrors = {};

    if (!newPassword) {
      newErrors.newPassword = "New password is required.";
    }
    if (!confirmNewPassword) {
      newErrors.confirmNewPassword = "Please confirm your new password.";
    }
    if (newPassword && confirmNewPassword && newPassword !== confirmNewPassword) {
      newErrors.confirmNewPassword = "Passwords do not match.";
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      setResetSuccess(true);
      setNewPassword("");
      setConfirmNewPassword("");
    }
  };

  // Back to Login handler resets all states
  const handleBackToLogin = () => {
    setResetSuccess(false);
    setEmailSent(false);
    setIsForgotPassword(false);
    setEmail("");
    setPassword("");
    setErrors({});
  };

  return (
    <div style={styles.body}>
      {/* Left Section */}
      <div style={styles.leftSection}>
        <h2 style={styles.leftTitle}>Medicore</h2>
        <img src="stethoscope.png.png" alt="Stethoscope" style={styles.image} />
        <div style={{ marginTop: 20, textAlign: "center" }}>
          <h3 style={styles.leftHeadline}>Smarter Care Starts Here</h3>
          <p style={styles.leftText}>
            MediCore unites patients and professionals—
            <br />
            simplifying care, records, and billing.
          </p>
        </div>
      </div>

      {/* Right Section */}
      <div style={styles.rightSection}>
        {/* Forgot Password Step 3: Create New Password */}
        {isForgotPassword && emailSent && !resetSuccess && (
          <div style={{ maxWidth: 400, width: "100%", textAlign: "left" }}>
            <h2 style={styles.rightTitle}>Create a New Password</h2>
            <form
              onSubmit={handleSubmitNewPassword}
              style={{ display: "flex", flexDirection: "column", gap: 16 }}
            >
              <div style={{ position: "relative" }}>
                <input
                  type={showNewPassword ? "text" : "password"}
                  placeholder="New Password"
                  style={{
                    ...styles.input,
                    borderColor: errors.newPassword ? "red" : "#27736f",
                  }}
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                />
                <span
                  style={styles.showPasswordToggle}
                  onClick={() => setShowNewPassword(!showNewPassword)}
                >
                  {showNewPassword ? "Hide" : "Show"}
                </span>
                {errors.newPassword && (
                  <p style={styles.errorText}>{errors.newPassword}</p>
                )}
              </div>

              <div style={{ position: "relative" }}>
                <input
                  type={showConfirmPassword ? "text" : "password"}
                  placeholder="Confirm New Password"
                  style={{
                    ...styles.input,
                    borderColor: errors.confirmNewPassword ? "red" : "#27736f",
                  }}
                  value={confirmNewPassword}
                  onChange={(e) => setConfirmNewPassword(e.target.value)}
                />
                <span
                  style={styles.showPasswordToggle}
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                >
                  {showConfirmPassword ? "Hide" : "Show"}
                </span>
                {errors.confirmNewPassword && (
                  <p style={styles.errorText}>{errors.confirmNewPassword}</p>
                )}
              </div>

              <button type="submit" style={styles.actionButton}>
                Reset Password
              </button>
            </form>

            <p
              style={styles.linkText}
              onClick={handleBackToLogin}
            >
              Cancel and go back to Login
            </p>
          </div>
        )}

        {/* Forgot Password Step 2: Email Sent Confirmation, before new password */}
        {isForgotPassword && emailSent && !resetSuccess && (
          <div style={{ maxWidth: 400, width: "100%", textAlign: "center" }}>
            <div style={styles.emailIconWrapper}>
              <MdEmail style={styles.emailIcon} />
            </div>

            <h2 style={styles.emailSentTitle}>Email Sent</h2>
            <p style={styles.emailSentText}>
              An email with password reset link has been sent to <b>{email}</b>. Open it to reset your password.
            </p>

            <button
              style={styles.actionButton}
              onClick={handleOpenEmail}
              type="button"
            >
              Open Email
            </button>

            <p style={styles.resendText}>
              Didn't receive an email?{" "}
              <span style={styles.resendLink} onClick={handleResendEmail}>
                Resend Email
              </span>
            </p>

            <p
              style={styles.linkText}
              onClick={handleBackToLogin}
            >
              Back to Login
            </p>
          </div>
        )}

        {/* Forgot Password Step 2: Success message after password reset */}
        {isForgotPassword && emailSent && resetSuccess && (
          <div style={{ maxWidth: 400, width: "100%", textAlign: "center" }}>
            <div style={styles.emailIconWrapper}>
              <MdEmail style={styles.emailIcon} />
            </div>

            <h2 style={styles.emailSentTitle}>Password Reset Successful</h2>
            <p style={styles.emailSentText}>
              Your password has been successfully reset. You can now log in with your new password.
            </p>

            <button
              style={styles.actionButton}
              onClick={handleBackToLogin}
              type="button"
            >
              Back to Login
            </button>
          </div>
        )}

        {/* Forgot Password Step 1: Enter Email to send reset link */}
        {isForgotPassword && !emailSent && (
          <div style={{ maxWidth: 400, width: "100%" }}>
            <h2 style={styles.rightTitle}>Reset Password</h2>
            <p style={styles.rightSubText}>
              Enter your email to receive a password reset link
            </p>

            <form style={{ display: "flex", flexDirection: "column", gap: 16 }} onSubmit={handleResetPassword}>
              <input
                type="email"
                placeholder="Email"
                style={{
                  ...styles.input,
                  borderColor: errors.email ? "red" : "#27736f",
                }}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              {errors.email && <p style={styles.errorText}>{errors.email}</p>}

              <button
                type="submit"
                style={styles.actionButton}
              >
                Send Link
              </button>
            </form>

            <p
              style={styles.linkText}
              onClick={handleBackToLogin}
            >
              Back to Login
            </p>

            <p style={styles.signupText}>
              Don’t have an account?{" "}
              <span
                style={styles.createAccountLink}
                onClick={() => navigate("/signup")}
              >
                create one now
              </span>
            </p>
          </div>
        )}

        {/* Normal Login Form */}
        {!isForgotPassword && (
          <div style={{ maxWidth: 400, width: "100%" }}>
            <h2 style={styles.rightTitle}>Log in to Patient Portal</h2>
            <p style={styles.rightSubText}>
              One portal for all your healthcare needs.
            </p>

            <form style={{ display: "flex", flexDirection: "column", gap: 16 }} onSubmit={handleLogin}>
              <input
                type="email"
                placeholder="Email"
                style={{
                  ...styles.input,
                  borderColor: errors.email ? "red" : "#27736f",
                }}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              {errors.email && <p style={styles.errorText}>{errors.email}</p>}

              <div style={{ position: "relative", width: "100%" }}>
                <input
                  type={showPassword ? "text" : "password"}
                  placeholder="Password"
                  style={{
                    ...styles.input,
                    borderColor: errors.password ? "red" : "#27736f",
                  }}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
                <span
                  style={styles.showPasswordToggle}
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? "Hide" : "Show"}
                </span>
              </div>

              {errors.password && <p style={styles.errorText}>{errors.password}</p>}
              {errors.general && <p style={styles.errorText}>{errors.general}</p>}

              <button type="submit" style={styles.actionButton}>
                Log in
              </button>
            </form>

            <p style={{ margin: "20px 0 8px", fontSize: 13, color: "#555", textAlign: "center" }}>
              Log in with
            </p>

            <div style={styles.socialIconsContainer}>
              <div style={{ ...styles.socialIcon, backgroundColor: "#1f7a6d" }}>
                <FaGoogle />
              </div>
              <div style={{ ...styles.socialIcon, backgroundColor: "#1f7a6d" }}>
                <FaFacebookF />
              </div>
              <div style={{ ...styles.socialIcon, backgroundColor: "#1f7a6d" }}>
                <FaApple />
              </div>
              <div style={{ ...styles.socialIcon, backgroundColor: "#1f7a6d" }}>
                <FaWindows />
              </div>
            </div>

            <p
              style={styles.linkText}
              onClick={() => {
                setErrors({});
                setIsForgotPassword(true);
                setEmail("");
                setPassword("");
              }}
            >
              Forgot password?
            </p>

            <p style={styles.signupText}>
              Don’t have an account?{" "}
              <span
                style={styles.createAccountLink}
                onClick={() => navigate("/signup")}
              >
                create one now
              </span>
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

