import React, { useState } from "react";

function PatientLogin() {
  const [step, setStep] = useState("login"); // "login" | "forgot" | "linkSent" | "reset"
  const [email, setEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isResent, setIsResent] = useState(false);
  
  // --- NEW STATE ---
  // This state will track if the password input has been focused
  const [isPasswordFocused, setIsPasswordFocused] = useState(false);

  // Simulate sending reset link
  const handleSendLink = (e) => {
    e.preventDefault();
    if (email.trim() !== "") {
      setStep("linkSent");
    }
  };

  const handleResend = () => {
    setIsResent(true);
    setTimeout(() => setIsResent(false), 2000);
  };

  const handleResetPassword = (e) => {
    e.preventDefault();
    const isLetter = /[A-Za-z]/.test(newPassword);
    const isNumber = /\d/.test(newPassword);
    const isSpecial = /[^A-Za-z0-9]/.test(newPassword);
    const isLength = newPassword.length >= 8;

    if (isLetter && isNumber && isSpecial && isLength && newPassword === confirmPassword) {
      alert("Password reset successfully!");
      setStep("login");
      setNewPassword("");
      setConfirmPassword("");
      setIsPasswordFocused(false); // Reset focus state
    } else {
      alert("Please meet all password requirements and ensure both passwords match.");
    }
  };

  return (
    <div style={styles.container}>
      {/* LEFT SIDE */}
      <div style={styles.left}>
        <h2 style={{ color: "white" }}>Medicore</h2>
        <img
          src="image.png" // Using a placeholder image URL
          alt="stethoscope"
          style={{ width: "90%", margin: "20px 0" }}
        />
        <h3 style={{ color: "white" }}>Smarter Care Starts Here</h3>
        <p style={{ color: "#cfe8e4" }}>
          MediCore unites patients and professionals — simplifying care, records, and billing.
        </p>
        <div className="carousel-dots">
          <div className="dot active"></div>
          <div className="dot"></div>
          <div className="dot"></div>
        </div>
      </div>

      {/* RIGHT SIDE */}
      <div style={styles.right}>
        {step === "login" && (
          <>
            <h2>Log in to Patient Portal</h2>
            <form style={styles.form}>
              <input
                type="email"
                placeholder="Email"
                style={styles.input}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <input
                type="password"
                placeholder="Password"
                style={styles.input}
              />
              <button style={styles.button}>Log in</button>
            </form>
            <p style={styles.link} onClick={() => setStep("forgot")}>
              Forgot Password?
            </p>
            <p style={{ fontSize: "13px" }}>
              Don’t have an account?{" "}
              <span style={styles.link} onClick={() => {
                  setStep("reset");
                  setIsPasswordFocused(false); // Ensure rules are hidden initially
              }}>
                Create one now
              </span>
            </p>
          </>
        )}

        {step === "forgot" && (
          <>
            <h2>Reset Password</h2>
            <p style={{ fontSize: "14px" }}>
              Enter your email to receive a password reset link.
            </p>
            <form style={styles.form} onSubmit={handleSendLink}>
              <input
                type="email"
                placeholder="Enter your email"
                style={styles.input}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <button style={styles.button} type="submit">
                Send Link
              </button>
            </form>
            <p style={styles.link} onClick={() => setStep("login")}>
              Back to Login
            </p>
          </>
        )}

        {step === "linkSent" && (
          <div style={styles.emailSentBox}>
            <img
              src="https://cdn-icons-png.flaticon.com/512/561/561127.png"
              alt="email icon"
              style={styles.icon}
            />
            <h2>Email Sent</h2>
            <p style={styles.description}>
              An email with a password reset link has been sent to{" "}
              <b>{email}</b>. Open it to reset your password.
            </p>
            <button
              style={styles.openButton}
              onClick={() => {
                setStep("reset");
                setIsPasswordFocused(false); // Ensure rules are hidden initially
              }}
            >
              Open Email
            </button>
            <p style={styles.resendText}>
              Didn’t receive email?{" "}
              <span style={styles.resendLink} onClick={handleResend}>
                Resend Email
              </span>
            </p>
            {isResent && <p style={styles.feedback}>Email resent successfully!</p>}
          </div>
        )}

        {step === "reset" && (
          <>
            <h2>Create a New Password</h2>
            <p style={{ fontSize: "14px" }}>
              Enter and confirm your new password to regain access to your Patient Portal.
            </p>
            <form style={styles.form} onSubmit={handleResetPassword}>
              <div style={{ position: "relative" }}>
                <input
                  type={showNewPassword ? "text" : "password"}
                  placeholder="New Password"
                  style={{ ...styles.input, width: "350px" }}
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  // --- ADDED ONFOCUS HANDLER ---
                  onFocus={() => setIsPasswordFocused(true)}
                />
                <span
                  style={styles.show}
                  onClick={() => setShowNewPassword(!showNewPassword)}
                >
                  {showNewPassword ? "Hide" : "Show"}
                </span>
              </div>

              {/* --- CONDITIONALLY RENDERED BLOCK --- */}
              {isPasswordFocused && (
                <div style={styles.passwordRulesContainer}>
                  <p style={styles.rulesTitle}>Your password must contain atleast</p>
                  <ul style={styles.passwordRules}>
                    <li style={{ color: /[A-Za-z]/.test(newPassword) ? "#1f7a6d" : "#555" }}>1 letter</li>
                    <li style={{ color: /\d/.test(newPassword) ? "#1f7a6d" : "#555" }}>1 number</li>
                    <li style={{ color: /[^A-Za-z0-9]/.test(newPassword) ? "#1f7a6d" : "#555" }}>1 Special character</li>
                    <li style={{ color: newPassword.length >= 8 ? "#1f7a6d" : "#555" }}>8 characters</li>
                  </ul>
                </div>
              )}

              <div style={{ position: "relative" }}>
                <input
                  type={showConfirmPassword ? "text" : "password"}
                  placeholder="Confirm New Password"
                  style={{ ...styles.input, width: "350px" }}
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                />
                <span
                  style={styles.show}
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                >
                  {showConfirmPassword ? "Hide" : "Show"}
                </span>
              </div>

              <button style={styles.button} type="submit">
                Reset Password
              </button>
            </form>
            <p style={styles.link} onClick={() => setStep("login")}>
              Cancel and go back to Login
            </p>
          </>
        )}
      </div>
    </div>
  );
}

