import React, { useState } from "react";

function PatientLogin() {
  const [step, setStep] = useState("login"); // "login" | "forgot" | "reset"
  const [email, setEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  // Simulate sending reset link
  const handleSendLink = (e) => {
    e.preventDefault();
    if (email.trim() !== "") {
      setStep("reset"); // Go to reset password page
    }
  };

  const handleResetPassword = (e) => {
    e.preventDefault();
    if (newPassword && confirmPassword && newPassword === confirmPassword) {
      alert("Password reset successfully!");
      setStep("login");
      setNewPassword("");
      setConfirmPassword("");
    }
  };

  return (
    <div style={styles.container}>
      {/* LEFT SIDE */}
      <div style={styles.left}>
        <h2 style={{ color: "white" }}>Medicore</h2>
        <img
          src="stethoscope.png.png"
          alt="stethoscope"
          style={{ width: "90%", margin: "40px 0" }}
        />
        <h3 style={{ color: "white" }}>Smarter Care Starts Here</h3>
        <p style={{ color: "#cfe8e4" }}>
          MediCore unites patients and professionals — simplifying care, records, and billing.
        </p>
        <div className="carousel-dots">
          <div className="dot active"></div>
          <div className="dot"></div>
          <div className="dot"></div>
        </div>
      </div>

      {/* RIGHT SIDE */}
      <div style={styles.right}>
        {step === "login" && (
          <>
            <h2>Log in to Patient Portal</h2>
            <form style={styles.form}>
              <input
                type="email"
                placeholder="Email"
                style={styles.input}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <input
                type="password"
                placeholder="Password"
                style={styles.input}
              />
              <button style={styles.button}>Log in</button>
            </form>
            <p
              style={styles.link}
              onClick={() => setStep("forgot")}
            >
              Forgot Password?
            </p>
            <p style={{ fontSize: "13px" }}>
              Don’t have an account?{" "}
              <span
                style={styles.link}
                onClick={() => setStep("reset")}
              >
                Create one now
              </span>
            </p>
          </>
        )}

        {step === "forgot" && (
          <>
            <h2>Reset Password</h2>
            <p style={{ fontSize: "14px" }}>
              Enter your email to receive a password reset link.
            </p>
            <form style={styles.form} onSubmit={handleSendLink}>
              <input
                type="email"
                placeholder="Enter your email"
                style={styles.input}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <button style={styles.button} type="submit">
                Send Link
              </button>
            </form>
            <p style={styles.link} onClick={() => setStep("login")}>
              Back to Login
            </p>
          </>
        )}

        {step === "reset" && (
          <>
            <h2>Create a New Password</h2>
            <p style={{ fontSize: "14px" }}>
              Enter and confirm your new password to regain access.
            </p>
            <form style={styles.form} onSubmit={handleResetPassword}>
              <div style={{ position: "relative" }}>
                <input
                  type={showNewPassword ? "text" : "password"}
                  placeholder="New Password"
                  style={{ ...styles.input, width: "350px" }}
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                />
                <span
                  style={styles.show}
                  onClick={() => setShowNewPassword(!showNewPassword)}
                >
                  {showNewPassword ? "Hide" : "Show"}
                </span>
              </div>
              <div style={{ position: "relative" }}>
                <input
                  type={showConfirmPassword ? "text" : "password"}
                  placeholder="Confirm New Password"
                  style={{ ...styles.input, width: "350px" }}
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                />
                <span
                  style={styles.show}
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                >
                  {showConfirmPassword ? "Hide" : "Show"}
                </span>
              </div>
              <button style={styles.button} type="submit">
                Reset Password
              </button>
            </form>
            <p style={styles.link} onClick={() => setStep("login")}>
              Cancel and go back to Login
            </p>
          </>
        )}
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    height: "100vh",
    fontFamily: "Arial, sans-serif",
  },
  left: {
    width: "45%",
    background: "#1f7a6d",
    padding: "40px",
    color: "white",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
  },
  right: {
    width: "55%",
    background: "white",
    padding: "40px",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    maxWidth: "350px",
    gap: "15px",
  },
  input: {
    padding: "12px",
    border: "1px solid #1f7a6d",
    borderRadius: "10px",
    fontSize: "14px",
    outline: "none",
  },
  button: {
    padding: "12px",
    background: "#1f7a6d",
    color: "white",
    border: "none",
    borderRadius: "15px",
    cursor: "pointer",
    fontSize: "16px",
  },
  link: {
    color: "#1f7a6d",
    cursor: "pointer",
    marginTop: "10px",
    fontSize: "13px",
  },
  dot: {
    display: "inline-block",
    width: "10px",
    height: "10px",
    borderRadius: "50%",
    background: "white",
    margin: "0 3px",
  },
  show: {
    position: "absolute",
    right: "12px",
    top: "50%",
    transform: "translateY(-50%)",
    fontSize: "13px",
    color: "#1f7a6d",
    cursor: "pointer",
  },
};

export default PatientLogin;
