import React from "react";
import { useNavigate } from "react-router-dom";
import "./RoleSelection.css"; // reuse same styles for dots & left panel

function PatientLogin() {
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    // Add login validation here
    alert("Patient Login Successful!");
    navigate("/dashboard"); // change this to your actual dashboard route
  };

  return (
    <div style={styles.body}>
      {/* Left Section */}
      <div style={styles.leftSection}>
        <h1 style={styles.leftTitle}>Medicore</h1>
        <img src="stethoscope.png.png" alt="Stethoscope" style={styles.image} />
        <h2 style={styles.leftSubtitle}>Smarter Care Starts Here</h2>
        <p style={styles.leftText}>
          MediCore unites patients and professionals — simplifying care, records, and billing.
        </p>
        <div className="carousel-dots">
          <div className="dot active"></div>
          <div className="dot"></div>
          <div className="dot"></div>
        </div>
      </div>

      {/* Right Section */}
      <div style={styles.rightSection}>
        <h1 style={styles.rightTitle}>Log in to Patient Portal</h1>
        <p style={styles.rightText}>
          One portal for all your healthcare needs.
        </p>

        <form style={styles.form} onSubmit={handleLogin}>
          <input type="email" placeholder="Email" style={styles.input} required />
          <div style={styles.passwordWrapper}>
            <input type="password" placeholder="Password" style={styles.input} required />
            <span style={styles.showPassword}>Show</span>
          </div>
          <button type="submit" style={styles.loginButton}>Log in</button>
        </form>

        <p style={styles.loginWithText}>Log in with</p>
        <div style={styles.socialIcons}>
          <i className="fab fa-google"></i>
          <i className="fab fa-facebook"></i>
          <i className="fab fa-apple"></i>
          <i className="fab fa-windows"></i>
        </div>

        <p style={styles.forgotPassword}>Forgot password?</p>
        <p style={styles.signupText}>
          Don’t have an account? <span style={styles.createLink}>create one now</span>
        </p>
      </div>
    </div>
  );
}

const styles = {
  body: {
    display: "flex",
    height: "100vh",
    backgroundColor: "#f5f5f5",
    fontFamily: "Montserrat, Arial, sans-serif"
  },
  leftSection: {
    width: "45%",
    backgroundColor: "#1f7a6d",
    color: "white",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    textAlign: "justify",
    paddingLeft: "40px",
    position: "relative"
  },
  image: {
    width: "90%",
    margin: "20px 0"
  },
  leftTitle: {
    fontSize: "28px",
    marginBottom: "10px"
  },
  leftSubtitle: {
    marginBottom: "10px"
  },
  leftText: {
    fontSize: "15px",
    color: "#dcdcdc",
    maxWidth: "350px"
  },
  rightSection: {
    width: "5%",
    backgroundColor: "white",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    padding: "40px",
    textAlign: "center"
  },
  rightTitle: {
    fontSize: "24px",
    marginBottom: "10px",
    fontWeight: "bold"
  },
  rightText: {
    fontSize: "14px",
    color: "#0a4a3f",
    marginBottom: "20px",
    maxWidth: "350px"
  },
  form: {
    width: "100%",
    maxWidth: "350px",
    display: "flex",
    flexDirection: "column"
  },
  input: {
    padding: "12px",
    margin: "10px 0",
    border: "1px solid #ccc",
    borderRadius: "10px",
    fontSize: "14px"
  },
  passwordWrapper: {
    position: "relative",
    width: "100%"
  },
  showPassword: {
    position: "absolute",
    right: "10px",
    top: "50%",
    transform: "translateY(-50%)",
    color: "#1f7a6d",
    cursor: "pointer",
    fontSize: "12px"
  },
  loginButton: {
    width: "100%",
    padding: "12px",
    margin: "20px 0",
    border: "none",
    borderRadius: "15px",
    background: "#1f7a6d",
    color: "white",
    fontSize: "16px",
    cursor: "pointer",
    transition: "all 0.3s ease"
  },
  loginWithText: {
    fontSize: "13px",
    color: "#333",
    margin: "10px 0"
  },
  socialIcons: {
    display: "flex",
    gap: "15px",
    fontSize: "20px",
    color: "#1f7a6d",
    marginBottom: "20px"
  },
  forgotPassword: {
    fontSize: "12px",
    color: "#1f7a6d",
    marginBottom: "10px",
    cursor: "pointer"
  },
  signupText: {
    fontSize: "12px",
    color: "#333"
  },
  createLink: {
    color: "#1f7a6d",
    cursor: "pointer"
  }
};

export default PatientLogin;
