import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaGoogle, FaFacebookF, FaApple, FaWindows } from "react-icons/fa";

function PatientLogin() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({});
  const [isForgotPassword, setIsForgotPassword] = useState(false);
  const [emailSent, setEmailSent] = useState(false);
  const [isCreatePassword, setIsCreatePassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [newPassword, setNewPassword] = useState("");
  const [confirmNewPassword, setConfirmNewPassword] = useState("");
  const [resetSuccess, setResetSuccess] = useState(false);

  // Login handler
  const handleLogin = (e) => {
    e.preventDefault();
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    let newErrors = {};

    if (!email) {
      newErrors.email = "Email address is required.";
    } else if (!emailPattern.test(email)) {
      newErrors.email = "Invalid email format.";
    }

    if (!password) {
      newErrors.password = "Password is required.";
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      if (email === "abc@gmail.com" && password === "ABCabc123#") {
        alert("Login successful!");
        navigate("/dashboard");
      } else {
        setErrors({ general: "Incorrect email or password." });
      }
    }
  };

  // Forgot Password handler
  const handleResetPassword = (e) => {
    e.preventDefault();
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    let newErrors = {};

    if (!email) {
      newErrors.email = "Email address is required.";
    } else if (!emailPattern.test(email)) {
      newErrors.email = "Invalid email format.";
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      setEmailSent(true);
      setErrors({});
    }
  };

  // Handler for New Password submission
  const handleSubmitNewPassword = (e) => {
    e.preventDefault();
    let newErrors = {};
    if (!newPassword) {
      newErrors.newPassword = "New password is required.";
    }
    if (!confirmNewPassword) {
      newErrors.confirmNewPassword = "Please confirm your new password.";
    }
    if (newPassword && confirmNewPassword && newPassword !== confirmNewPassword) {
      newErrors.confirmNewPassword = "Passwords do not match.";
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      setResetSuccess(true);
      setNewPassword("");
      setConfirmNewPassword("");
    }
  };

  // Handler to go back to login
  const handleBackToLogin = () => {
    setResetSuccess(false);
    setEmailSent(false);
    setIsForgotPassword(false);
    setIsCreatePassword(false);
    setEmail("");
    setPassword("");
    setErrors({});
  };

  return (
    <div style={styles.body}>
      {/* Left Section */}
      <div style={styles.leftSection}>
        <h1 style={styles.leftTitle}>Medicore</h1>
        <h3 style={styles.leftSubtitle}>Smarter Care Starts Here</h3>
        <p style={styles.leftText}>
          MediCore unites patients and professionals—<br />simplifying care, records, and billing.
        </p>
        <div style={styles.sliderDots}>
          <div style={{ ...styles.dot, backgroundColor: "white" }}></div>
          <div style={styles.dot}></div>
          <div style={styles.dot}></div>
        </div>
      </div>

      {/* Right Section */}
      <div style={styles.rightSection}>
        {/* Create New Password (after Send Link or Create one now) */}
        {isForgotPassword && (isCreatePassword || (emailSent && !resetSuccess)) && (
          <div style={styles.rightContent}>
            <h1 style={styles.rightTitle}>
              Create a New Password
            </h1>
            <p style={styles.rightDesc}>
              Enter and confirm your new password to regain access to your Patient Portal.
            </p>
            <form onSubmit={handleSubmitNewPassword} style={{ display: "flex", flexDirection: "column", gap: "15px", width: "100%", maxWidth: 400 }}>
              <div style={{ position: "relative", width: "100%" }}>
                <input
                  type={showNewPassword ? "text" : "password"}
                  placeholder="New Password"
                  style={{
                    ...styles.inputOutlined,
                    borderColor: errors.newPassword ? "red" : "#2B6A67"
                  }}
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                />
                <span
                  style={styles.showPasswordRight}
                  onClick={() => setShowNewPassword(!showNewPassword)}
                >
                  {showNewPassword ? "Hide" : "Show"}
                </span>
                {errors.newPassword && <p style={styles.error}>{errors.newPassword}</p>}
              </div>
              <div style={{ position: "relative", width: "100%" }}>
                <input
                  type={showConfirmPassword ? "text" : "password"}
                  placeholder="Confirm New Password"
                  style={{
                    ...styles.inputOutlined,
                    borderColor: errors.confirmNewPassword ? "red" : "#2B6A67"
                  }}
                  value={confirmNewPassword}
                  onChange={(e) => setConfirmNewPassword(e.target.value)}
                />
                <span
                  style={styles.showPasswordRight}
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                >
                  {showConfirmPassword ? "Hide" : "Show"}
                </span>
                {errors.confirmNewPassword && <p style={styles.error}>{errors.confirmNewPassword}</p>}
              </div>
              <button type="submit" style={styles.resetButton}>
                Reset Password
              </button>
            </form>

            <p
              style={styles.cancelLink}
              onClick={handleBackToLogin}
            >
              Cancel and go back to Login
            </p>
          </div>
        )}

        {/* Login Form */}
        {!isForgotPassword && (
          <>
            <h1 style={styles.rightTitle}>Log in to Patient Portal</h1>
            <p style={styles.rightDesc}>One portal for all your healthcare needs.</p>

            <form style={styles.form} onSubmit={handleLogin}>
              <input
                type="email"
                placeholder="Email"
                style={{
                  ...styles.inputOutlined,
                  borderColor: errors.email ? "red" : "#2B6A67",
                }}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              {errors.email && <p style={styles.error}>{errors.email}</p>}

              <div style={{ position: "relative", width: "100%" }}>
                <input
                  type={showPassword ? "text" : "password"}
                  placeholder="Password"
                  style={{
                    ...styles.inputOutlined,
                    borderColor: errors.password ? "red" : "#2B6A67",
                  }}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
                <span
                  style={styles.showPasswordRight}
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? "Hide" : "Show"}
                </span>
              </div>
              {errors.password && <p style={styles.error}>{errors.password}</p>}
              {errors.general && <p style={styles.error}>{errors.general}</p>}

              <button type="submit" style={styles.loginButton}>
                Log in
              </button>
            </form>

            <p style={styles.loginWithText}>Log in with</p>
            <div style={styles.socialIcons}>
              <div style={{ ...styles.icon, background: "#2B6A67" }}>
                <FaGoogle />
              </div>
              <div style={{ ...styles.icon, background: "#2B6A67" }}>
                <FaFacebookF />
              </div>
              <div style={{ ...styles.icon, background: "#2B6A67" }}>
                <FaApple />
              </div>
              <div style={{ ...styles.icon, background: "#2B6A67" }}>
                <FaWindows />
              </div>
            </div>

            <p
              style={styles.forgotPassword}
              onClick={() => {
                setErrors({});
                setIsForgotPassword(true);
                setEmail("");
                setPassword("");
              }}
            >
              Forgot password?
            </p>

            <p style={styles.signupText}>
              Don’t have an account?{" "}
              <span
                style={styles.createLink}
                onClick={() => {
                  setIsForgotPassword(true);
                  setIsCreatePassword(true);
                }}
              >
                create one now
              </span>
            </p>
          </>
        )}
      </div>
    </div>
  );
}

