const CheckmarkIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="white" width="12px" height="12px">
    <path d="M9.55 18l-5-5 1.45-1.45L9.55 15.1l7.05-7.05L18 9.55z" />
  </svg>
);


function PatientLogin() {
  const [step, setStep] = useState("login"); // "login" | "forgot" | "linkSent" | "reset"
  const [email, setEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isResent, setIsResent] = useState(false);
  const [isPasswordFocused, setIsPasswordFocused] = useState(false);

  // Password validation checks
  const hasLetter = /[A-Za-z]/.test(newPassword);
  const hasNumber = /\d/.test(newPassword);
  const hasSpecial = /[^A-Za-z0-9]/.test(newPassword);
  const has8Chars = newPassword.length >= 8;

  const handleSendLink = (e) => {
    e.preventDefault();
    if (email.trim() !== "") {
      setStep("linkSent");
    }
  };

  const handleResend = () => {
    setIsResent(true);
    setTimeout(() => setIsResent(false), 2000);
  };

  const handleResetPassword = (e) => {
    e.preventDefault();
    if (hasLetter && hasNumber && hasSpecial && has8Chars && newPassword === confirmPassword) {
      alert("Password reset successfully!");
      setStep("login");
      setNewPassword("");
      setConfirmPassword("");
      setIsPasswordFocused(false);
    } else {
      alert("Please meet all password requirements and ensure both passwords match.");
    }
  };

  return (
    <div style={styles.container}>
      {/* LEFT SIDE */}
      <div style={styles.left}>
        <h2 style={{ color: "white" }}>Medicore</h2>
        <img
          src="https://i.imgur.com/gYf24bY.png" // Placeholder image
          alt="stethoscope"
          style={{ width: "90%", margin: "20px 0" }}
        />
        <h3 style={{ color: "white" }}>Smarter Care Starts Here</h3>
        <p style={{ color: "#cfe8e4" }}>
          MediCore unites patients and professionals — simplifying care, records, and billing.
        </p>
        <div style={styles.carouselDots}>
          <span style={{...styles.dot, ...styles.dotActive}}></span>
          <span style={styles.dot}></span>
          <span style={styles.dot}></span>
        </div>
      </div>

      {/* RIGHT SIDE */}
      <div style={styles.right}>
        {step === "login" && (
          <div style={styles.formContainer}>
            <h2>Log in to Patient Portal</h2>
            <form style={styles.form}>
              <input
                type="email"
                placeholder="Email"
                style={styles.input}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <input
                type="password"
                placeholder="Password"
                style={styles.input}
              />
              <button style={styles.button}>Log in</button>
            </form>
            <p style={styles.link} onClick={() => setStep("forgot")}>
              Forgot Password?
            </p>
            <p style={{ fontSize: "13px" }}>
              Don’t have an account?{" "}
              <span style={styles.link} onClick={() => {
                  setStep("reset");
                  setIsPasswordFocused(false);
              }}>
                Create one now
              </span>
            </p>
          </div>
        )}

        {step === "forgot" && (
          <div style={styles.formContainer}>
            <h2>Reset Password</h2>
            <p style={{ fontSize: "14px" }}>
              Enter your email to receive a password reset link.
            </p>
            <form style={styles.form} onSubmit={handleSendLink}>
              <input
                type="email"
                placeholder="Enter your email"
                style={styles.input}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <button style={styles.button} type="submit">
                Send Link
              </button>
            </form>
            <p style={styles.link} onClick={() => setStep("login")}>
              Back to Login
            </p>
          </div>
        )}

        {step === "linkSent" && (
          <div style={styles.emailSentBox}>
            <img
              src="https://cdn-icons-png.flaticon.com/512/561/561127.png"
              alt="email icon"
              style={styles.icon}
            />
            <h2>Email Sent</h2>
            <p style={styles.description}>
              An email with a password reset link has been sent to{" "}
              <b>{email}</b>. Open it to reset your password.
            </p>
            <button
              style={styles.openButton}
              onClick={() => {
                setStep("reset");
                setIsPasswordFocused(false);
              }}
            >
              Open Email
            </button>
            <p style={styles.resendText}>
              Didn’t receive email?{" "}
              <span style={styles.resendLink} onClick={handleResend}>
                Resend Email
              </span>
            </p>
            {isResent && <p style={styles.feedback}>Email resent successfully!</p>}
          </div>
        )}

        {step === "reset" && (
          <div style={styles.formContainer}>
            <h2>Create a New Password</h2>
            <p style={{ fontSize: "14px" }}>
              Enter and confirm your new password to regain access to your Patient Portal.
            </p>
            <form style={styles.form} onSubmit={handleResetPassword}>
              <div style={{ position: "relative" }}>
                <input
                  type={showNewPassword ? "text" : "password"}
                  placeholder="New Password"
                  style={{ ...styles.input, width: "350px" }}
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  onFocus={() => setIsPasswordFocused(true)}
                />
                <span
                  style={styles.show}
                  onClick={() => setShowNewPassword(!showNewPassword)}
                >
                  {showNewPassword ? "Hide" : "Show"}
                </span>
              </div>

              {isPasswordFocused && (
                <div style={styles.passwordRulesContainer}>
                  <p style={styles.rulesTitle}>Your password must contain atleast</p>
                  <ul style={styles.passwordRules}>
                    <li style={styles.ruleItem}>
                      <span style={{...styles.bullet, ...(hasLetter ? styles.bulletMet : styles.bulletNotMet)}}>
                        {hasLetter && <CheckmarkIcon />}
                      </span>
                      <span style={{color: hasLetter ? '#2D706E' : '#555'}}>1 letter</span>
                    </li>
                    <li style={styles.ruleItem}>
                      <span style={{...styles.bullet, ...(hasNumber ? styles.bulletMet : styles.bulletNotMet)}}>
                         {hasNumber && <CheckmarkIcon />}
                      </span>
                      <span style={{color: hasNumber ? '#2D706E' : '#555'}}>1 number</span>
                    </li>
                    <li style={styles.ruleItem}>
                       <span style={{...styles.bullet, ...(hasSpecial ? styles.bulletMet : styles.bulletNotMet)}}>
                         {hasSpecial && <CheckmarkIcon />}
                      </span>
                      <span style={{color: hasSpecial ? '#2D706E' : '#555'}}>1 Special character</span>
                    </li>
                     <li style={styles.ruleItem}>
                      <span style={{...styles.bullet, ...(has8Chars ? styles.bulletMet : styles.bulletNotMet)}}>
                        {has8Chars && <CheckmarkIcon />}
                      </span>
                      <span style={{color: has8Chars ? '#2D706E' : '#555'}}>8 characters</span>
                    </li>
                  </ul>
                </div>
              )}

              <div style={{ position: "relative" }}>
                <input
                  type={showConfirmPassword ? "text" : "password"}
                  placeholder="Confirm New Password"
                  style={{ ...styles.input, width: "350px" }}
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                />
                <span
                  style={styles.show}
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                >
                  {showConfirmPassword ? "Hide" : "Show"}
                </span>
              </div>

              <button style={styles.button} type="submit">
                Reset Password
              </button>
            </form>
            <p style={styles.link} onClick={() => setStep("login")}>
              Cancel and go back to Login
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    height: "100vh",
    fontFamily: "'Poppins', sans-serif",
  },
  left: {
    width: "45%",
    background: "#2D706E",
    padding: "40px",
    color: "white",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-around",
    alignItems: "center",
    textAlign: "center",
  },
  right: {
    width: "55%",
    background: "white",
    padding: "40px",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    textAlign: "center",
  },
  formContainer: {
    width: '100%',
    maxWidth: '350px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center'
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "20px",
    width: "100%",
    maxWidth: "350px",
    marginTop: "20px"
  },
  input: {
    padding: "15px 20px",
    border: "1px solid #B2D8D6",
    borderRadius: "25px",
    fontSize: "14px",
    outline: "none",
  },
  button: {
    padding: "15px",
    background: "#2D706E",
    color: "white",
    border: "none",
    borderRadius: "25px",
    cursor: "pointer",
    fontSize: "16px",
    fontWeight: "bold",
  },
  emailSentBox: {
    maxWidth: "400px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  icon: {
    width: "80px",
    marginBottom: "15px",
  },
  description: {
    fontSize: "14px",
    color: "#333",
    marginBottom: "20px",
  },
  openButton: {
    padding: "12px 30px",
    background: "#2D706E",
    color: "white",
    border: "none",
    borderRadius: "20px",
    cursor: "pointer",
    fontSize: "16px",
    marginBottom: "10px",
  },
  resendText: {
    fontSize: "13px",
    color: "#444",
  },
  resendLink: {
    color: "#2D706E",
    cursor: "pointer",
    textDecoration: "underline",
  },
  feedback: {
    marginTop: "10px",
    fontSize: "13px",
    color: "green",
  },
  show: {
    position: "absolute",
    right: "20px",
    top: "50%",
    transform: "translateY(-50%)",
    fontSize: "13px",
    color: "#2D706E",
    cursor: "pointer",
    fontWeight: "bold",
  },
  link: {
    color: "#2D706E",
    cursor: "pointer",
    marginTop: "15px",
    fontSize: "13px",
    fontWeight: "bold",
  },
  passwordRulesContainer: {
    textAlign: "left",
    width: "100%",
    maxWidth: "350px",
    padding: '0 10px',
    marginTop: '-10px',
    marginBottom: '10px'
  },
  rulesTitle: {
    fontSize: '13px',
    color: '#333',
    marginBottom: '8px',
    fontWeight: 'normal', // Ensure text is not bold
  },
  passwordRules: {
    padding: "0",
    listStyleType: "none",
    display: 'flex',
    flexDirection: 'column',
    gap: '4px',
  },
  ruleItem: {
    display: 'flex',
    alignItems: 'center',
    gap: '10px',
  },
  bullet: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    width: '14px',
    height: '14px',
    borderRadius: '50%',
    boxSizing: 'border-box',
    transition: 'all 0.2s ease-in-out',
  },
  bulletNotMet: {
    border: '1px solid #B2D8D6',
    backgroundColor: 'white',
  },
  bulletMet: {
    border: '1px solid #2D706E',
    backgroundColor: '#2D706E',
  },
  carouselDots: {
    display: 'flex',
    gap: '10px',
  },
  dot: {
    width: '8px',
    height: '8px',
    borderRadius: '50%',
    backgroundColor: 'rgba(255, 255, 255, 0.4)',
  },
  dotActive: {
    backgroundColor: 'white',
  },
};

export default PatientLogin;
