import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaGoogle, FaFacebookF, FaApple, FaWindows } from "react-icons/fa";
import { MdEmail } from "react-icons/md";
import "./RoleSelection.css";

function PatientLogin() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({});
  const [isForgotPassword, setIsForgotPassword] = useState(false);
  const [emailSent, setEmailSent] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [newPassword, setNewPassword] = useState("");
  const [confirmNewPassword, setConfirmNewPassword] = useState("");
  const [resetSuccess, setResetSuccess] = useState(false);

  const handleLogin = (e) => {
    e.preventDefault();
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    let newErrors = {};

    if (!email) newErrors.email = "Email address is required.";
    else if (!emailPattern.test(email)) newErrors.email = "Invalid email format.";

    if (!password) newErrors.password = "Password is required.";

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      if (email === "abc@gmail.com" && password === "ABCabc123#") {
        alert("Login successful!");
        navigate("/dashboard");
      } else {
        setErrors({ general: "Incorrect email or password." });
      }
    }
  };

  return (
    <div style={styles.body}>
      {/* Left Section */}
      <div style={styles.leftSection}>
        <h1 style={styles.leftTitle}>Medicore</h1>
        <img src="stethoscope.png.png" alt="Stethoscope" style={styles.image} />
        <p style={styles.leftText}>
          Your Journey to Better Health <br />
          Stay informed—access records, reports, appointments and connect with doctors securely.
        </p>

        {/* Dots Section */}
        <div style={styles.dotsContainer}>
          <div style={styles.rectangleDot}></div>
          <div style={styles.circleDot}></div>
          <div style={styles.circleDot}></div>
        </div>
      </div>

      {/* Right Section */}
      <div style={styles.rightSection}>
        <h1 style={styles.rightTitle}>Log in to Patient Portal</h1>
        <p style={styles.rightText}>One portal for all your healthcare needs.</p>

        <form style={styles.form} onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Email"
            style={{ ...styles.input, border: errors.email ? "1px solid red" : "1px solid #1f7a6d" }}
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          {errors.email && <p style={styles.error}>{errors.email}</p>}

          <div style={styles.passwordWrapper}>
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              style={{
                ...styles.input,
                border: errors.password ? "1px solid red" : "1px solid #1f7a6d",
              }}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <span
              style={styles.showPassword}
              onClick={() => setShowPassword(!showPassword)}
            >
              {showPassword ? "Hide" : "Show"}
            </span>
          </div>
          {errors.password && <p style={styles.error}>{errors.password}</p>}

          {errors.general && <p style={styles.error}>{errors.general}</p>}

          <button type="submit" style={styles.loginButton}>
            Log in
          </button>
        </form>
      </div>
    </div>
  );
}

const styles = {
  body: {
    display: "flex",
    height: "100vh",
    backgroundColor: "#f5f5f5",
    fontFamily: "Montserrat, Arial, sans-serif",
  },
  leftSection: {
    width: "45%",
    backgroundColor: "#1f7a6d",
    color: "white",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    padding: "40px",
    position: "relative",
  },
  leftTitle: {
    fontSize: "28px",
    marginBottom: "20px",
  },
  image: {
    width: "90%",
    margin: "20px 0",
  },
  leftText: {
    fontSize: "15px",
    color: "#dcdcdc",
    maxWidth: "350px",
  },
  dotsContainer: {
    display: "flex",
    alignItems: "center",
    gap: "8px",
    marginTop: "20px",
  },
  rectangleDot: {
    width: "40px",
    height: "12px",
    backgroundColor: "#ffffff",
    borderRadius: "6px",
  },
  circleDot: {
    width: "12px",
    height: "12px",
    backgroundColor: "rgba(255,255,255,0.5)",
    borderRadius: "50%",
  },
  rightSection: {
    width: "55%",
    backgroundColor: "white",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    padding: "40px",
    textAlign: "center",
  },
  rightTitle: {
    fontSize: "24px",
    marginBottom: "10px",
    fontWeight: "bold",
  },
  rightText: {
    fontSize: "14px",
    color: "#0a4a3f",
    marginBottom: "20px",
    maxWidth: "350px",
  },
  form: {
    width: "100%",
    maxWidth: "350px",
    display: "flex",
    flexDirection: "column",
  },
  input: {
    width: "350px",
    padding: "12px",
    margin: "10px 0",
    borderRadius: "12px",
    fontSize: "14px",
    outline: "none",
  },
  passwordWrapper: {
    position: "relative",
    width: "350px",
  },
  showPassword: {
    position: "absolute",
    right: "15px",
    top: "50%",
    transform: "translateY(-50%)",
    color: "#1f7a6d",
    cursor: "pointer",
    fontSize: "14px",
  },
  loginButton: {
    width: "350px", // Fixed width as requested
    padding: "12px",
    margin: "20px 0",
    border: "none",
    borderRadius: "15px",
    background: "#1f7a6d",
    color: "white",
    fontSize: "16px",
    cursor: "pointer",
    transition: "all 0.3s ease",
  },
  error: {
    color: "red",
    fontSize: "13px",
    margin: "-5px 0 10px 0",
    textAlign: "left",
  },
};

export default PatientLogin;
