import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaGoogle, FaFacebookF, FaApple, FaWindows } from "react-icons/fa";
import { MdEmail } from "react-icons/md"; // For email icon
import "./RoleSelection.css";

function PatientLogin() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({});
  const [isForgotPassword, setIsForgotPassword] = useState(false);
  const [emailSent, setEmailSent] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [newPassword, setNewPassword] = useState("");
  const [confirmNewPassword, setConfirmNewPassword] = useState("");
  const [resetSuccess, setResetSuccess] = useState(false);

  // Login handler
  const handleLogin = (e) => {
    e.preventDefault();
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    let newErrors = {};

    if (!email) {
      newErrors.email = "Email address is required.";
    } else if (!emailPattern.test(email)) {
      newErrors.email = "Invalid email format.";
    }

    if (!password) {
      newErrors.password = "Password is required.";
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      if (email === "abc@gmail.com" && password === "ABCabc123#") {
        alert("Login successful!");
        navigate("/dashboard");
      } else {
        setErrors({ general: "Incorrect email or password." });
      }
    }
  };

  // Forgot password: send reset link
  const handleResetPassword = (e) => {
    e.preventDefault();
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    let newErrors = {};

    if (!email) {
      newErrors.email = "Email address is required.";
    } else if (!emailPattern.test(email)) {
      newErrors.email = "Invalid email format.";
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      // Simulate sending email
      setEmailSent(true);
      setErrors({});
    }
  };

  // Resend email link handler
  const handleResendEmail = () => {
    alert(`Password reset link resent to ${email}`);
  };

  // Open user's email provider
  const handleOpenEmail = () => {
    const domain = email.split("@")[1];
    let mailUrl = "https://mail.google.com"; // fallback
    if (domain?.includes("gmail.com")) mailUrl = "https://mail.google.com";
    else if (domain?.includes("yahoo.com")) mailUrl = "https://mail.yahoo.com";
    else if (
      domain?.includes("outlook.com") ||
      domain?.includes("hotmail.com")
    )
      mailUrl = "https://outlook.live.com";
    else if (domain?.includes("icloud.com")) mailUrl = "https://www.icloud.com/mail";

    window.open(mailUrl, "_blank");
  };

  // Submit new password form
  const handleSubmitNewPassword = (e) => {
    e.preventDefault();
    let newErrors = {};
    if (!newPassword) {
      newErrors.newPassword = "New password is required.";
    }
    if (!confirmNewPassword) {
      newErrors.confirmNewPassword = "Please confirm your new password.";
    }
    if (newPassword && confirmNewPassword && newPassword !== confirmNewPassword) {
      newErrors.confirmNewPassword = "Passwords do not match.";
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      // Simulate password reset success
      setResetSuccess(true);
      setNewPassword("");
      setConfirmNewPassword("");
    }
  };

  // Back to Login from any forgot/reset step
  const handleBackToLogin = () => {
    setResetSuccess(false);
    setEmailSent(false);
    setIsForgotPassword(false);
    setEmail("");
    setPassword("");
    setErrors({});
  };

  return (
    <div style={styles.body}>
      {/* Left Section */}
      <div style={styles.leftSection}>
        <h1 style={styles.leftTitle}>Medicore</h1>
        <img src="stethoscope.png.png" alt="Stethoscope" style={styles.image} />
        <p style={styles.leftText}>
          Smarter Care Starts Here
          <br />
          MediCore unites patients and professionals—simplifying care, records, and billing.
        </p>
      </div>

      {/* Right Section */}
      <div style={styles.rightSection}>
        {/* Step 3: Create New Password */}
        {isForgotPassword && emailSent && !resetSuccess && (
          <div style={{ maxWidth: "400px", padding: "20px", textAlign: "left" }}>
            <h1 style={styles.rightTitle}>Create a New Password</h1>
            <form
              onSubmit={handleSubmitNewPassword}
              style={{ display: "flex", flexDirection: "column", gap: "15px" }}
            >
              <div style={{ position: "relative" }}>
                <input
                  type={showNewPassword ? "text" : "password"}
                  placeholder="New Password"
                  style={{
                    ...styles.input,
                    width: "100%",
                    border: errors.newPassword ? "1px solid red" : "1px solid #1f7a6d",
                  }}
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                />
                <span
                  style={{
                    ...styles.showPassword,
                    right: "12px",
                    top: "50%",
                    transform: "translateY(-50%)",
                  }}
                  onClick={() => setShowNewPassword(!showNewPassword)}
                >
                  {showNewPassword ? "Hide" : "Show"}
                </span>
                {errors.newPassword && <p style={styles.error}>{errors.newPassword}</p>}
              </div>

              <div style={{ position: "relative" }}>
                <input
                  type={showConfirmPassword ? "text" : "password"}
                  placeholder="Confirm New Password"
                  style={{
                    ...styles.input,
                    width: "100%",
                    border: errors.confirmNewPassword ? "1px solid red" : "1px solid #1f7a6d",
                  }}
                  value={confirmNewPassword}
                  onChange={(e) => setConfirmNewPassword(e.target.value)}
                />
                <span
                  style={{
                    ...styles.showPassword,
                    right: "12px",
                    top: "50%",
                    transform: "translateY(-50%)",
                  }}
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                >
                  {showConfirmPassword ? "Hide" : "Show"}
                </span>
                {errors.confirmNewPassword && (
                  <p style={styles.error}>{errors.confirmNewPassword}</p>
                )}
              </div>

              <button type="submit" style={styles.loginButton}>
                Reset Password
              </button>
            </form>

            <p
              style={{ ...styles.forgotPassword, marginTop: "15px", textAlign: "center" }}
              onClick={handleBackToLogin}
            >
              Cancel and go back to Login
            </p>
          </div>
        )}

        {/* Step 2: Email Sent Confirmation */}
        {isForgotPassword && emailSent && resetSuccess && (
          <div style={{ maxWidth: "400px", padding: "20px", textAlign: "center" }}>
            <div style={styles.emailIconWrapper}>
              <MdEmail style={styles.emailIcon} />
            </div>
            <h1 style={styles.emailSentTitle}>Password Reset Successful</h1>
            <p style={styles.emailSentText}>
              Your password has been successfully reset. You can now log in with your new password.
            </p>
            <button
              style={styles.openEmailButton}
              onClick={handleBackToLogin}
              type="button"
            >
              Back to Login
            </button>
          </div>
        )}

        {/* Step 2: Email Sent Confirmation (before entering new password) */}
        {isForgotPassword && emailSent && !resetSuccess && (
          <div style={{ maxWidth: "400px", padding: "20px", textAlign: "center" }}>
            <div style={styles.emailIconWrapper}>
              <MdEmail style={styles.emailIcon} />
            </div>
            <h1 style={styles.emailSentTitle}>Email Sent</h1>
            <p style={styles.emailSentText}>
              An email with password reset link has been sent to <b>{email}</b>. Open it to reset your password.
            </p>
            <button style={styles.openEmailButton} onClick={handleOpenEmail} type="button">
              Open Email
            </button>

            <p style={styles.resendContainer}>
              Didn't receive an email?{" "}
              <span style={styles.resendLink} onClick={handleResendEmail}>
                Resend Email
              </span>
            </p>

            <p
              style={{ ...styles.forgotPassword, marginTop: "10px" }}
              onClick={handleBackToLogin}
            >
              Back to Login
            </p>
          </div>
        )}

        {/* Step 1: Forgot Password Form */}
        {isForgotPassword && !emailSent && (
          <>
            <h1 style={styles.rightTitle}>Reset Password</h1>
            <p style={{ ...styles.rightText, marginBottom: "20px" }}>
              Enter your email to receive a password reset link
            </p>

            <form style={styles.form} onSubmit={handleResetPassword}>
              <input
                type="email"
                placeholder="Email"
                style={{
                  ...styles.input,
                  border: errors.email ? "1px solid red" : "1px solid #1f7a6d",
                }}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              {errors.email && <p style={styles.error}>{errors.email}</p>}

              <button type="submit" style={styles.loginButton}>
                Send Link
              </button>
            </form>

            <p
              style={{ ...styles.forgotPassword, marginTop: "10px" }}
              onClick={handleBackToLogin}
            >
              Back to Login
            </p>

            <p style={styles.signupText}>
              Don’t have an account?{" "}
              <span style={styles.createLink} onClick={() => navigate("/signup")}>
                create one now
              </span>
            </p>
          </>
        )}

        {/* Normal Login Form */}
        {!isForgotPassword && (
          <>
            <h1 style={styles.rightTitle}>Log in to Patient Portal</h1>
            <p style={styles.rightText}>One portal for all your healthcare needs.</p>

            <form style={styles.form} onSubmit={handleLogin}>
              <input
                type="email"
                placeholder="Email"
                style={{
                  ...styles.input,
                  border: errors.email ? "1px solid red" : "1px solid #1f7a6d",
                }}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              {errors.email && <p style={styles.error}>{errors.email}</p>}

              <div style={styles.passwordWrapper}>
                <input
                  type={showPassword ? "text" : "password"}
                  placeholder="Password"
                  style={{
                    ...styles.input,
                    border: errors.password ? "1px solid red" : "1px solid #1f7a6d",
                  }}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
                <span
                  style={styles.showPassword}
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? "Hide" : "Show"}
                </span>
              </div>
              {errors.password && <p style={styles.error}>{errors.password}</p>}

              {errors.general && <p style={styles.error}>{errors.general}</p>}

              <button type="submit" style={styles.loginButton}>
                Log in
              </button>
            </form>

            <p style={styles.loginWithText}>Log in with</p>
            <div style={styles.socialIcons}>
              <div style={{ ...styles.icon, background: "#1f7a6d" }}>
                <FaGoogle />
              </div>
              <div style={{ ...styles.icon, background: "#1f7a6d" }}>
                <FaFacebookF />
              </div>
              <div style={{ ...styles.icon, background: "#1f7a6d" }}>
                <FaApple />
              </div>
              <div style={{ ...styles.icon, background: "#1f7a6d" }}>
                <FaWindows />
              </div>
            </div>

            <p
              style={styles.forgotPassword}
              onClick={() => {
                setErrors({});
                setIsForgotPassword(true);
                setEmail("");
                setPassword("");
              }}
            >
              Forgot password?
            </p>

            <p style={styles.signupText}>
              Don’t have an account?{" "}
              <span style={styles.createLink} onClick={() => navigate("/signup")}>
                create one now
              </span>
            </p>
          </>
        )}
      </div>
    </div>
  );
}

