import React from "react";

function RoleSelection() {
  return (
    <div style={styles.container}>
      {/* Left Side */}
      <div style={styles.leftPanel}>
        <h1 style={styles.logo}>Medicore</h1>
        <img
          src="stethoscope.png" 
          alt="Stethoscope"
          style={styles.image}
        />
        <h3>Smarter Care Starts Here</h3>
        <p>MediCore unites patients and professionalsâ€”simplifying care, records, and billing.</p>
      </div>

      {/* Right Side */}
      <div style={styles.rightPanel}>
        <h2>Welcome to MediCore</h2>
        <p>Select your role below to access your secure healthcare dashboard.</p>
        <div style={styles.buttons}>
          <button style={styles.button}>I'm Patient</button>
          <button style={styles.button}>I'm Staff</button>
          <button style={styles.button}>I'm Admin</button>
        </div>
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    height: "100vh",
    fontFamily: "Arial, sans-serif"
  },
  leftPanel: {
    flex: 1,
    background: "#1B7C72",
    color: "#fff",
    padding: "40px",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    textAlign: "center"
  },
//   logo: {
//     fontSize: "2rem",
//     marginBottom: "20px"
//   },
  image: {
    width: "150px",
    marginBottom: "20px"
  },
  rightPanel: {
    flex: 1,
    background: "#fff",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    padding: "40px",
    textAlign: "center"
  },
  buttons: {
    marginTop: "20px",
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    width: "200px"
  },
  button: {
    padding: "12px",
    border: "2px solid #1B7C72",
    borderRadius: "8px",
    background: "transparent",
    color: "#1B7C72",
    fontWeight: "bold",
    cursor: "pointer",
    transition: "all 0.3s ease"
  }
};

export default RoleSelection;
