    gap: '10px',
    fontSize: '13px'
  },
  bullet: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    width: '14px',
    height: '14px',
    borderRadius: '50%',
    boxSizing: 'border-box',
    transition: 'all 0.2s ease-in-out'
  },
  bulletNotMet: {
    border: '1px solid #B2D8D6',
    backgroundColor: 'white'
  },
  bulletMet: {
    border: '1px solid #2D706E',
    backgroundColor: '#2D706E'
  },
  // --- CAROUSEL DOTS ---
  carouselDots: {
    display: 'flex',
    gap: '10px'
  },
  dot: {
    width: '8px',
    height: '8px',
    borderRadius: '50%',
    backgroundColor: 'rgba(255, 255, 255, 0.4)',
    transition: 'all 0.3s ease'
  },
  dotActive: {
    backgroundColor: 'white',
    width: '24px',
    borderRadius: '4px'
  }
};
